package com.sxit.model.common;

/**
 * 用于映射Mybatis 需要的map中间对象
 * 
 * @author agu
 * 
 */
public class IntegerMap {
	private Integer id;

	private String name;

	public IntegerMap() {
		super();
		// Auto-generated constructor stub
	}

	public IntegerMap(Integer id, String name) {
		super();
		this.id = id;
		this.name = name;
	}

	public Integer getId() {
		return id;
	}

	public void setId(Integer id) {
		this.id = id;
	}

	public String getName() {
		return name;
	}

	public void setName(String name) {
		this.name = name;
	}
}
