mapper.update:
update sys_user set user_id = #{userId}, user_name = #{userName},
password = #{password},
name = #{name}, mobile = #{mobile}, logo = #{logo}, mail = #{mail}, remark = #{remark}, post = #{post}, org_id = #{orgId}, org_name = #{orgName}, user_type = #{userType}, state = #{state}, create_time = #{createTime}, modify_time = #{modifyTime} where user_id = #{userId}

